package com.strizhevskiy.factbookweb.Repositories;import com.strizhevskiy.factbookweb.model.CountryMain;import org.springframework.data.jpa.repository.Query;import org.springframework.data.repository.CrudRepository;public interface CountryMainRepository extends CrudRepository<CountryMain, Integer> {    CountryMain findCountryMainByCountryNameAndDataYear(String countryName, int dataYear);    CountryMain findByCountryCodeAndDataYear(String countryCode, int dataYear);    @Query(value = "SELECT CountryName FROM CountryMain WHERE DataYear = 2017 ORDER BY CountryMain.CountryName", nativeQuery = true)    Iterable<String> listCountries();}