package com.strizhevskiy.factbookweb.Repositories;import com.strizhevskiy.factbookweb.model.CountryMain;import org.springframework.data.jpa.repository.Query;import org.springframework.data.repository.CrudRepository;public interface CountryMainRepository extends CrudRepository<CountryMain, Integer> {    CountryMain findCountryMainByCountryName(String countryName);    @Query(value = "SELECT CountryName FROM CountryMain ORDER BY CountryMain.CountryName", nativeQuery = true)    Iterable<String> listCountries();}